import React from "react";
import "./HeroSection.css";
import HeroImg from "../assets/backgroundimg.png";
import Home from "../assets/Home2.png";
import why from "../assets/why.png";
import process from "../assets/process.png";
function HeroSection() {
  return (
    <>
      <div
        className="herosection"
        style={{
          backgroundImage: `url(${HeroImg})`,
          height: "75vh",
          width: "100%",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <h1>How our service will work</h1>
        <div
          style={{
            marginTop: "5vh",
            display: "flex",
            boxShadow: "rgb(0 0 0 / 35%) 0px 5px 15px",

            height: "16vh",
            width: "70vw",
            justifyContent: "center",
            borderRadius: 8,
          }}
        >
          <div
            style={{
              justifyContent: "center",
              display: "flex",
              marginRight: 75,

              flexDirection: "column",
            }}
          >
            <h1
              style={{
                color: "#59A70A",
              }}
            >
              Sell Your Scrap
            </h1>
            <h5>In just 4 easy steps...</h5>
          </div>
          <div
            style={{
              display: "flex",

              alignItems: "center",
              borderRadius: 5,
              paddingLeft: 5,
              width: "28vw",
              overflow: "hidden",
              alignSelf: "center",
              backgroundColor: "rgba(0,0,0,0.2)",
            }}
          >
            <div
              style={{
                marginRight: 15,
                marginLeft: 5,
              }}
            >
              <i style={{}} class="fas fa-mobile-alt"></i>
            </div>
            <div
              style={{
                color: "yellow",

                height: "5vh",
                alignItems: "center",
                display: "flex",
              }}
            >
              <input
                style={{
                  backgroundColor: "transparent",
                  borderWidth: 0,
                  color: "white",
                  outline: "none",
                }}
                type="text"
                required
                placeholder="Enter your Mobile No"
                autoComplete="off"
                title="Full Name is required"
              ></input>
            </div>
          </div>
          <button
            style={{
              height: "5vh",
              alignSelf: "center",
              marginLeft: 8,
              paddingInline: 20,
            }}
          >
            Proceed
          </button>

          <div
            style={{
              overflow: "visible",
            }}
          >
            <h5
              style={{
                textAlign: "center",
                backgroundColor: "green",
                position: "absolute",
                paddingInline: 8,
                paddingTop: 4.5,
                paddingBottom: 4.5,
                left: "40vw",
                top: "43.7vh",
                color: "white",
                borderRadius: 500,
              }}
            >
              01
            </h5>
            <h6
              style={{
                textAlign: "center",
                color: "green",
                position: "absolute",
                left: "39vw",
                top: "47.7vh",
              }}
            >
              Mobile No
            </h6>
          </div>
          <div>
            <h5
              style={{
                textAlign: "center",
                backgroundColor: "green",
                position: "absolute",
                paddingInline: 8,
                paddingTop: 4.5,
                paddingBottom: 4.5,
                left: "47vw",
                top: "43.7vh",
                color: "white",
                borderRadius: 500,
              }}
            >
              02
            </h5>
            <h6
              style={{
                textAlign: "center",
                color: "green",
                position: "absolute",
                left: "46.7vw",
                top: "47.7vh",
              }}
            >
              Address
            </h6>
          </div>
          <div>
            <h5
              style={{
                textAlign: "center",
                backgroundColor: "green",
                position: "absolute",
                paddingInline: 8,
                paddingTop: 4.5,
                paddingBottom: 4.5,
                left: "54vw",
                top: "43.7vh",
                color: "white",
                borderRadius: 500,
              }}
            >
              03
            </h5>
            <h6
              style={{
                textAlign: "center",
                color: "green",
                position: "absolute",
                left: "53.5vw",
                top: "47.7vh",
              }}
            >
              Schedule
            </h6>
          </div>
          <div>
            <h5
              style={{
                textAlign: "center",
                backgroundColor: "green",
                position: "absolute",
                paddingInline: 8,
                paddingTop: 4.5,
                paddingBottom: 4.5,
                left: "61vw",
                top: "43.7vh",
                color: "white",
                borderRadius: 500,
              }}
            >
              04
            </h5>
            <h6
              style={{
                textAlign: "center",
                color: "green",
                position: "absolute",
                left: "60vw",
                top: "47.7vh",
              }}
            >
              Sell Scrap
            </h6>
          </div>
        </div>
      </div>

      <div
        style={{
          marginBottom: "10vh",
          marginTop: "10vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <img
          style={{
            width: "90vw",
          }}
          src={Home}
        ></img>
        <img
          style={{
            marginTop: "20vh",
            width: "80vw",
          }}
          src={why}
        ></img>
        <img
          style={{
            marginTop: "20vh",
            width: "90vw",
          }}
          src={process}
        ></img>
      </div>
    </>
  );
}

export default HeroSection;
