import React from "react";
import HeroImg from "../assets/backgroundimg.png";

function HeroSection() {
  return (
    <div
      style={{
        backgroundImage: `url(${HeroImg})`,
        height: "75vh",
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <h1>How our service will work</h1>
      <div
        style={{
          display: "flex",
          boxShadow: "rgb(0 0 0 / 35%) 0px 5px 15px",
          paddingRight: "5vw",
          paddingLeft: "5vw",
        }}
      >
        <div
          style={{
            width: "20vw",
            justifyContent: "center",
            display: "flex",
            flexDirection: "column",
          }}
        >
          <h3
            style={{
              color: "#59A70A",
            }}
          >
            Sell Your Scrap
          </h3>
          <h5>In just 4 easy steps...</h5>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            borderRadius: 5,
            paddingLeft: 5,
            overflow: "hidden",
            alignSelf: "center",
            backgroundColor: "rgba(0,0,0,0.2)",
          }}
        >
          <div
            style={{
              marginRight: 5,
            }}
          >
            <i style={{}} class="fas fa-mobile-alt"></i>
          </div>
          <div
            style={{
              color: "yellow",
            }}
          >
            <input
              style={{
                backgroundColor: "transparent",
                borderWidth: 0,
                color: "yellow",
              }}
              type="text"
              required
              placeholder="Enter your Mobile No"
              autoComplete="off"
              title="Full Name is required"
            ></input>
          </div>
        </div>

        <div>
          <h3
            style={{
              backgroundColor: "green",
            }}
          >
            01
          </h3>
          <h6>Mobile No</h6>
        </div>
        <div>
          <h3>02</h3>
          <h6>Address</h6>
        </div>
        <div>
          <h3>03</h3>
          <h6>Schedule</h6>
        </div>
        <div>
          <h3>04</h3>
          <h6>Sell Scrap</h6>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
