import React from "react";
import HeroImg from "../assets/backgroundimg.png";

function HeroSection() {
  return (
    <div
      style={{
        backgroundImage: `url(${HeroImg})`,
        height: "75vh",
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <h1>How our service will work</h1>
      <div
        style={{
          display: "flex",
          boxShadow: "rgb(0 0 0 / 35%) 0px 5px 15px",
          paddingRight: "5vw",
          paddingLeft: "5vw",
        }}
      >
        <div
          style={{
            width: "50%",
            heignt: "10vh",
          }}
        >
          <h3
            style={{
              color: "white",
            }}
          >
            Sell Your Scrap
          </h3>
          <h5>In just 4 easy steps...</h5>
        </div>
        <div
          style={{
            backgroundColor: "black",
            width: "20%",
            height: "10vh",
          }}
        >
          <h1>ff</h1>
        </div>
      </div>
    </div>
  );
}

export default HeroSection;
