import React, { useState, useEffect } from "react";
import "./HeroSection.css";
import HeroImg from "../assets/backgroundimg.png";
import Home from "../assets/Home2.png";
import why from "../assets/why.png";
import process from "../assets/process.png";
function HeroSection() {
  const width = window.innerWidth;
  const [visible, setVisible] = useState(true);

  const setdata = () => {
    if (width > 940) {
      setVisible(false);
    }
  };
  useEffect(() => {
    setdata();
    console.log("fdfdv", width);
  }, []);
  return (
    <>
      <div
        className="herosection"
        style={{
          backgroundImage: `url(${HeroImg})`,
        }}
      >
        <h2 className="heading">How our service will work</h2>
        <div
          style={{
            marginTop: "5vh",
            display: "flex",
            boxShadow: "rgb(0 0 0 / 35%) 0px 5px 15px",

            height: "16vh",
            width: "70vw",
            justifyContent: "center",
            borderRadius: 8,
          }}
        >
          <div className="setItem">
            <h1
              style={{
                color: "#59A70A",
              }}
            >
              Sell Your Scrap
            </h1>
            <h5 className="Easystep">In just 4 easy steps...</h5>
          </div>
          <div className="inputfeild">
            <div
              style={{
                marginLeft: 5,
              }}
            >
              <i class="fas fa-mobile-alt"></i>
            </div>
            <div
              style={{
                color: "yellow",
                marginLeft: 5,
                height: "5vh",
                alignItems: "center",
                display: "flex",
              }}
            >
              <input
                style={{
                  backgroundColor: "transparent",
                  borderWidth: 0,
                  color: "white",
                  outline: "none",
                }}
                type="text"
                required
                placeholder="Enter your Mobile No"
                autoComplete="off"
                title="Full Name is required"
              ></input>
            </div>
          </div>
          <button className="button">Proceed</button>
          {visible ? null : (
            <>
              <div
                style={{
                  overflow: "visible",
                }}
              >
                <h5
                  style={{
                    textAlign: "center",
                    backgroundColor: "green",
                    position: "absolute",
                    paddingInline: 8,
                    paddingTop: 4.5,
                    paddingBottom: 4.5,
                    left: "40vw",
                    top: "43.7vh",
                    color: "white",
                    borderRadius: 500,
                  }}
                >
                  01
                </h5>
                <h6
                  style={{
                    textAlign: "center",
                    color: "green",
                    position: "absolute",
                    left: "39vw",
                    top: "47.7vh",
                  }}
                >
                  Mobile No
                </h6>
              </div>
              <div>
                <h5
                  style={{
                    textAlign: "center",
                    backgroundColor: "green",
                    position: "absolute",
                    paddingInline: 8,
                    paddingTop: 4.5,
                    paddingBottom: 4.5,
                    left: "47vw",
                    top: "43.7vh",
                    color: "white",
                    borderRadius: 500,
                  }}
                >
                  02
                </h5>
                <h6
                  style={{
                    textAlign: "center",
                    color: "green",
                    position: "absolute",
                    left: "46.7vw",
                    top: "47.7vh",
                  }}
                >
                  Address
                </h6>
              </div>
              <div>
                <h5
                  style={{
                    textAlign: "center",
                    backgroundColor: "green",
                    position: "absolute",
                    paddingInline: 8,
                    paddingTop: 4.5,
                    paddingBottom: 4.5,
                    left: "54vw",
                    top: "43.7vh",
                    color: "white",
                    borderRadius: 500,
                  }}
                >
                  03
                </h5>
                <h6
                  style={{
                    textAlign: "center",
                    color: "green",
                    position: "absolute",
                    left: "53.5vw",
                    top: "47.7vh",
                  }}
                >
                  Schedule
                </h6>
              </div>
              <div>
                <h5
                  style={{
                    textAlign: "center",
                    backgroundColor: "green",
                    position: "absolute",
                    paddingInline: 8,
                    paddingTop: 4.5,
                    paddingBottom: 4.5,
                    left: "61vw",
                    top: "43.7vh",
                    color: "white",
                    borderRadius: 500,
                  }}
                >
                  04
                </h5>
                <h6
                  style={{
                    textAlign: "center",
                    color: "green",
                    position: "absolute",
                    left: "60vw",
                    top: "47.7vh",
                  }}
                >
                  Sell Scrap
                </h6>
              </div>
            </>
          )}
        </div>
      </div>

      <div
        style={{
          marginBottom: "10vh",
          marginTop: "10vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <img
          style={{
            width: "90vw",
          }}
          src={Home}
        ></img>
        <img
          style={{
            marginTop: "20vh",
            width: "80vw",
          }}
          src={why}
        ></img>
        <img
          style={{
            marginTop: "20vh",
            width: "90vw",
          }}
          src={process}
        ></img>
        <div
          style={{
            display: "flex",
          }}
        >
          <div
            class="badge-base LI-profile-badge"
            data-locale="en_US"
            data-size="medium"
            data-theme="dark"
            data-type="VERTICAL"
            data-vanity="rahul-bisht-08b1401b0"
            data-version="v1"
          >
            <a
              class="badge-base__link LI-simple-link"
              href="https://in.linkedin.com/in/rahul-bisht-08b1401b0?trk=profile-badge"
            ></a>
          </div>
          <div
            class="badge-base LI-profile-badge"
            data-locale="en_US"
            data-size="medium"
            data-theme="dark"
            data-type="VERTICAL"
            data-vanity="prince-vikal-4b1b34177"
            data-version="v1"
          >
            <a
              class="badge-base__link LI-simple-link"
              href="https://in.linkedin.com/in/prince-vikal-4b1b34177?trk=profile-badge"
            >
              Prince Vikal
            </a>
          </div>
        </div>
      </div>
    </>
  );
}

export default HeroSection;
